#!/bin/bash# 定义颜色BLUE_COLOR="\033[36m"RED_COLOR="\033[31m"GREEN_COLOR="\033[32m"VIOLET_COLOR="\033[35m"RES="\033[0m"echo -e "${BLUE_COLOR}# ######################################################################${RES}"echo -e "${BLUE_COLOR}#                       Docker Compose Shell Script                    #${RES}"echo -e "${BLUE_COLOR}#                       Blog: www.lmaye.com                            #${RES}"echo -e "${BLUE_COLOR}#                       Email: lmay@lmaye.com                          #${RES}"echo -e "${BLUE_COLOR}# ######################################################################${RES}"# 创建目录if [ ! -d "./nginx/" ]; thenecho -e "${VIOLET_COLOR}---> create [nginx] directory start.${RES}"mkdir -p ./nginx/conf ./nginx/logs ./nginx/www ./nginx/www/filesfiif [ ! -d "./minio/" ]; thenecho -e "${VIOLET_COLOR}---> create [minio] directory start.${RES}"mkdir -p ./minio/conf ./minio/datafiif [ ! -d "./mongo/" ]; thenecho -e "${VIOLET_COLOR}---> create [mongo] directory start.${RES}"mkdir -p ./mongo/data ./mongo/logsfiecho -e "${BLUE_COLOR}===> create directory success.${RES}"if [ -f "./nginx.conf" ]; thenecho -e "${VIOLET_COLOR}---> move [nginx] config file start.${RES}"mv ./nginx.conf ./nginx/confmv ./hexo-blog-admin ./nginx/wwwfiif [ -d "./hexo-blog-admin/" ]; thenecho -e "${VIOLET_COLOR}---> copy [hexo-blog-admin] project start.${RES}"# 项目移过去,主要为了访问静态资源。因为项目是以uwsgi部署, 所以访问静态资源需nginx配置\cp -rf ./hexo-blog-admin/ ./nginx/www/fiecho -e "${BLUE_COLOR}===> move config files success.${RES}"echo -e "${GREEN_COLOR}>>>>>>>>>>>>>>>>>> The End <<<<<<<<<<<<<<<<<<${RES}"# 部署项目echo -e "${BLUE_COLOR}==================> Docker deploy Start <==================${RES}"docker-compose up --build -d